import moment from 'moment';
import axios from 'axios';
import {CONFIG} from '../config.js'

const chain_id = `1`
const address = `0x4d224452801ACEd8B2F0aebE155379bb5D594381`
const bitDAO_address = `0x1A4b46696b2bB4794Eb3D4c26f1c55F9170fa4C5`


 export default async function getTokenHolders(chain_id, address){
    const days_interval = 30; 
    let from = moment().subtract(days_interval, 'days').format('YYYY-MM-DD')

    const currentDay =moment().format('YYYY-MM-DD')

    // Steps to get tokenholders in the past 7 days 
    // 1. Get block heights with start_date and end_date 2022-10-01 and 2022-10-31
    // 2. Take the begin
    let api_call_tokenholder = `https://api.covalenthq.com/v1/${chain_id}/tokens/${address}/token_holders_changes/?&key=ckey_af1d8bda66e340bd835f1855487`
    console.log("Hello")


    try{
     const resp = await axios.get(api_call_tokenholder, {auth: {username: CONFIG.TEMPLATE.api_key}})
     console.log(resp)
     // Organize response data to insert into graph
     let date_to_holders = resp.data.data.items.map(i => ({x:i.opening_date, y:i.floor_price_quote_7d})).reverse()
     let date_to_holders_same = resp.data.data.items.map(i => ({x:i.opening_date, y:i.floor_price_wei_7d / 10 ** 18})).reverse()

    }catch(error){
       console.log("fail to fetch token holders")
   }
  }
