     const resp= await axios.get(`https://api.covalenthq.com/v1/${blockchain_id}/nft_market/collection/${address_id}/`,{auth: {username: CONFIG.TEMPLATE.api_key}})
   }catch(error){}

   if (CONFIG.TEMPLATE.timeseries_chart){
