import { Web3Storage } from 'web3.storage'
import {CONFIG} from '../config.js'
import { sha256, sha224 } from 'js-sha256';
function getAccessToken () {
  // If you're just testing, you can paste in a token
  // and uncomment the following line:
  // return 'paste-your-token-here'

  // In a real app, it's better to read an access token from an
  // environement variable or other configuration that's kept outside of
  // your code base. For this to work, you need to set the
  // WEB3STORAGE_TOKEN environment variable before you run your code.
  return CONFIG.TEMPLATE.web3storage_api_key
}

function makeStorageClient () {
  return new Web3Storage({ token: getAccessToken() })
}

export default async function storeContent (){
const content = {
  "name": "dao_terminal_v0",
  "version": "0.1.0",
  "private": true,
  "dependencies": {
    "@livepeer/webrtmp-sdk": "^0.2.3",
    "@testing-library/jest-dom": "^5.16.5",
    "@testing-library/react": "^13.4.0",
    "@testing-library/user-event": "^13.5.0",
    "axios": "^1.1.3",
    "chart.js": "^3.9.1",
    "moment": "^2.29.4",
    "react": "^18.2.0",
    "react-chartjs-2": "^4.3.1",
    "react-dom": "^18.2.0",
    "react-scripts": "5.0.1",
    "web-vitals": "^2.1.4",
    "web3.storage": "^4.4.0"
  }}

  const storage = new Web3Storage({ token: getAccessToken()});
  const finalContent = JSON.stringify(content);
  const file = new File([finalContent], await sha256(finalContent), {
      type: 'text/plain',
    }); 
  const cid = await storage.put([file]);
  return cid;

  }
