// import logo from './logo.svg';
import './App.css';
import {useState, useEffect} from "react"
import React from 'react';
import axios from 'axios';
// import TimeSeriesChart from './timeseriesChart';
 import moment from 'moment';
function App() {
 const [items, setItems] = useState([])

 // setup for Timeseries graph
 const [graphData, setGraph] = useState([])
 const [weiData, setWei] = useState([])
 const [graphErr, setErr] = useState(false)


 //call getData funciton use React Hook
 useEffect(() => {
   getData()
 }, [])

 const handleGraph = async(filter) => {
   setErr(false)
   setGraph([])
   setWei([])
   let from = moment().subtract(filter, 'days').format('YYYY-MM-DD')

   const blockchain_id = "1"
   const address_id = "0x4d224452801ACEd8B2F0aebE155379bb5D594381"
   const currentDay =moment().format('YYYY-MM-DD')

   let api_call = filter > 0 ? 
   // 2 dates (from - to)
   `https://api.covalenthq.com/v1/${blockchain_id}/nft_market/collection/${address_id}/?from=${from}&to=${currentDay}` 
   : 
   // 1 date (current date - all data before it)
   `https://api.covalenthq.com/v1/${blockchain_id}/nft_market/collection/${address_id}/?to=${currentDay}`

   // Request for floor prices and add parameters to format for graph
   try{
    const resp = await axios.get(api_call, {auth: {username: CONFIG.TEMPLATE.api_key}})

    // Organize response data to insert into graph
    setGraph(resp.data.data.items.map(i => ({x:i.opening_date, y:i.floor_price_quote_7d})).reverse())
    setWei(resp.data.data.items.map(i => ({x:i.opening_date, y:i.floor_price_wei_7d / 10 ** 18})).reverse())
    setErr(false)
  }catch(error){
      setErr(true)
  }
 }
 // get data for Chain names and Chain heights
 const getData = async() => {
   try {
     const response = await axios.get('https://api.covalenthq.com/v1/chains/status/?key=ckey_af1d8bda66e340bd835f1855487');
     console.log(response.data.data);
     setItems(response.data.data.items)
        } catch (err) {
     // Handle Error Here
     console.error(err);
    }
 }

 // Use APE coin as an example to get token holders
 const getTokenHolders = async() => {
   try {
     const response = await axios.get('https://api.covalenthq.com/v1/chains/status/?key=ckey_af1d8bda66e340bd835f1855487');
     console.log(response.data.data);
     setItems(response.data.data.items)
        } catch (err) {
     // Handle Error Here
     console.error(err);
    }
 }



 return (
   <div className="App">
       {items.map(item =>(
         <div key={item.chain_id}>
           <h5>{item.name}</h5>
           <h5>{item.synced_block_height}</h5>
         </div>
       ))}
   </div>
 );
}

export default App;
